let modal=document.querySelector(".modal"),openModalBtn=document.querySelectorAll(["[data-openModal]"]),closeModalBtn=document.querySelector("[data-closeModal]");function openModal(){modal.style.display="block",modal.style.overflow="hidden"}function closeModal(){modal.style.display="none",modal.style.overflow=""}openModalBtn.forEach((o=>o.addEventListener("click",openModal))),closeModalBtn.addEventListener("click",closeModal),modal.addEventListener("click",(o=>{o.target===modal&&closeModal()})),window.addEventListener("keydown",(o=>{"Escape"===o.key&&closeModal()}));let animItems=document.querySelectorAll(".jobs__item");function boxAnimate(){animItems.forEach((o=>{o.classList.add("jobs__item_animate")}))}function showModalByScroll(){window.scrollY>=animBox.offsetHeight-window.innerHeight&&(boxAnimate(),window.removeEventListener("scroll",showModalByScroll))}animBox=document.querySelector(".jobs__body"),window.addEventListener("scroll",showModalByScroll);